import { defineStore } from 'pinia'
import { ref } from 'vue'

export const useStateStore = defineStore('state', () => {
    const state = ref(3) // 默认 3（无权限）

    const setState = (newRole) => {
        role.value = newRole
    }

    const clearState = () => {
        role.value = 3
    }

    return {
        state,
        setState,
        clearState
    }
}, {
    persist: true // ✅ 持久化存储
})
