import { defineStore } from 'pinia'
import { ref } from 'vue'

export const useStateStore = defineStore('state', () => {
    const role = ref(3) // 默认 3（无权限）

    const setRole = (newRole) => {
        role.value = newRole
    }

    const clearRole = () => {
        role.value = 3
    }

    return {
        role,
        setRole,
        clearRole
    }
}, {
    persist: true // ✅ 持久化存储
})
