import "core-js/modules/es.array.push.js";
import { useRoute, useRouter } from 'vue-router';
import { creationInfo, likeInfo, likeCreation, collectInfo, collectCreation, listComment, editorial, delComment, delCreation, updateCreation, creationClassList } from '@/api/creation';
import { hairstyleInfo } from '@/api/hairstyle';
import { offlineInfo, memberList } from '@/api/offline';
import { ref, onMounted } from 'vue';
import { userCancelFollowService, userFollowInfoService, userFollowService, userOtherInfoService, userInfoService } from '@/api/user';
import { offlineCommentList, addReservation } from '@/api/reservation';
import { ElMessage, ElMessageBox } from 'element-plus';
import { Icon } from '@iconify/vue';
import { enableCache } from '@iconify/vue';
import RichTextEditor from '@/components/RichTextEditor.vue';
import { ArrowLeft, Delete, Edit } from '@element-plus/icons-vue';
import { uploadFile } from '@/api/fileUpload';
export default {
  __name: 'ContentPage',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    enableCache('all');
    const route = useRoute();
    const router = useRouter();
    const detail = ref({});
    const content = ref('');
    const userInfo = ref({});
    const followState = ref();
    const loading = ref(true);
    const followLoading = ref(false);
    const likeState = ref(1);
    const collectState = ref(1);
    const type = route.params.type;
    const id = route.params.id;
    const commentInput = ref('');
    const comments = ref([]);
    const commentTotal = ref(0);
    const commentPageNum = ref(1);
    const commentPageSize = ref(10);
    const loadingComments = ref(false);
    const currentUserId = ref(null);
    const commentEditorRef = ref(null);
    const editDialogVisible = ref(false);
    const form = ref({
      creation_id: null,
      title: '',
      abs_text: '',
      cover_pic: '',
      content: '',
      class_id: null
    });
    const classOptions = ref([]);
    const formLoading = ref(false);
    const fetchClassOptions = async () => {
      try {
        const res = await creationClassList({
          pageNum: 1,
          pageSize: 100
        });
        classOptions.value = res.data.items || [];
      } catch (err) {
        ElMessage.error('获取分类失败');
      }
    };
    const openEditDialog = async () => {
      // 1. 拉取数据并做 article→div 替换
      const res = await creationInfo(detail.value.creation_id);
      form.value = {
        ...res.data,
        content: res.data.content.replace(/<article[^>]*>/gi, '<div>').replace(/<\/article>/gi, '</div>')
      };
      // 2. 预加载分类
      await fetchClassOptions();
      // 3. 最后再打开对话框，让编辑器第一次挂载时就拿到完整的 HTML
      editDialogVisible.value = true;
    };
    const handleEditSubmit = async () => {
      const {
        title,
        abs_text,
        cover_pic,
        content,
        class_id
      } = form.value;
      if (!title || !abs_text || !cover_pic || !content || !class_id) {
        return ElMessage.warning('请完整填写所有信息');
      }
      formLoading.value = true;
      try {
        await updateCreation({
          ...form.value
        });
        ElMessage.success('保存成功');
        editDialogVisible.value = false;
        fetchData();
      } catch (err) {
        ElMessage.error('保存失败');
      } finally {
        formLoading.value = false;
      }
    };
    const handleCoverUpload = async ({
      file
    }) => {
      try {
        const res = await uploadFile(file);
        form.value.cover_pic = res.data;
        ElMessage.success('上传成功');
      } catch (e) {
        ElMessage.error('上传失败');
      }
    };
    const decodeHTML = htmlStr => {
      const textarea = document.createElement('textarea');
      textarea.innerHTML = htmlStr;
      return textarea.value;
    };
    const fetchLikeCollect = async () => {
      try {
        const [likeRes, collectRes] = await Promise.all([likeInfo(id), collectInfo(id)]);
        likeState.value = likeRes.data;
        collectState.value = collectRes.data;
        console.log(likeState.value + ";" + collectState.value);
      } catch (err) {
        console.error('获取点赞/收藏失败', err);
      }
    };
    const toggleLike = async () => {
      try {
        await likeCreation({
          creation_id: Number(id)
        });
        likeState.value = likeState.value === 0 ? 1 : 0;
        ElMessage.success(likeState.value === 0 ? '点赞成功' : '取消点赞成功');
      } catch (err) {
        ElMessage.error('操作失败');
      }
    };
    const toggleCollect = async () => {
      try {
        await collectCreation({
          creation_id: Number(id)
        });
        collectState.value = collectState.value === 0 ? 1 : 0;
        ElMessage.success(collectState.value === 0 ? '收藏成功' : '取消收藏成功');
      } catch (err) {
        ElMessage.error('操作失败');
      }
    };
    const handleDeleteCreation = async () => {
      try {
        await ElMessageBox.confirm('确定要删除该内容吗？删除后无法恢复！', '提示', {
          confirmButtonText: '删除',
          cancelButtonText: '取消',
          type: 'warning'
        });
        await delCreation(detail.value.creation_id);
        ElMessage.success('删除成功');
        router.push('/');
      } catch (err) {
        if (err !== 'cancel') {
          ElMessage.error('删除失败');
        }
      }
    };

    /**
     * 门店部分
     * */
    const offlineComments = ref([]);
    const offlineCommentTotal = ref(0);
    const offlineCommentPageNum = ref(1);
    const offlineCommentPageSize = ref(10);
    const loadingOfflineComments = ref(false);
    const userInfoMap = ref({});
    const reserveDialogVisible = ref(false);
    reserveForm.value = {
      member_id: null,
      date: '',
      start_time: '',
      end_time: ''
    };
    const memberOptions = ref([]);
    const reserveLoading = ref(false);
    const openReserveDialog = async () => {
      try {
        // 拉取门店成员列表
        const res = await memberList(id);
        memberOptions.value = res.data || [];

        // 默认起始时间是最近的半小时
        const now = new Date();
        now.setSeconds(0);
        now.setMilliseconds(0);
        let startHour = now.getHours();
        let startMinute = now.getMinutes();
        if (startMinute < 30) {
          startMinute = 30;
        } else {
          startHour += 1;
          startMinute = 0;
        }
        const start_at = `${String(startHour).padStart(2, '0')}:${String(startMinute).padStart(2, '0')}`;

        // 结束时间默认比开始时间晚半小时
        let endHour = startHour;
        let endMinute = startMinute + 30;
        if (endMinute >= 60) {
          endHour += 1;
          endMinute = 0;
        }
        const end_at = `${String(endHour).padStart(2, '0')}:${String(endMinute).padStart(2, '0')}`;
        reserveForm.value = {
          member_id: null,
          start_at,
          end_at
        };
        reserveDialogVisible.value = true;
      } catch (err) {
        ElMessage.error(err.msg || '获取门店成员失败');
      }
    };
    const timeOptions = ref([]);
    const generateTimeOptions = () => {
      const times = [];
      const now = new Date();
      now.setSeconds(0);
      now.setMilliseconds(0);
      now.setMinutes(now.getMinutes() < 30 ? 30 : 0);
      if (now.getMinutes() === 0) {
        now.setHours(now.getHours() + 1);
      }
      for (let i = 0; i < 48; i++) {
        // 24小时 * 2
        const h = String(now.getHours()).padStart(2, '0');
        const m = String(now.getMinutes()).padStart(2, '0');
        times.push(`${h}:${m}`);
        now.setMinutes(now.getMinutes() + 30);
      }
      timeOptions.value = times;
    };
    const submitReservation = async () => {
      if (!reserveForm.value.member_id || !reserveForm.value.start_at || !reserveForm.value.end_at) {
        ElMessage.warning('请完整填写预约信息');
        return;
      }
      const today = new Date();
      const yyyy = today.getFullYear();
      const MM = String(today.getMonth() + 1).padStart(2, '0');
      const dd = String(today.getDate()).padStart(2, '0');
      const start_at = `${yyyy}-${MM}-${dd} ${reserveForm.value.start_at}:00`;
      const end_at = `${yyyy}-${MM}-${dd} ${reserveForm.value.end_at}:00`;
      if (start_at >= end_at) {
        ElMessage.warning('结束时间必须晚于开始时间');
        return;
      }
      try {
        reserveLoading.value = true;
        await addReservation({
          offline_id: Number(id),
          member_id: reserveForm.value.member_id,
          start_at,
          end_at
        });
        ElMessage.success('预约成功！');
        reserveDialogVisible.value = false;
        reserveForm.value = {
          member_id: null,
          start_at: '',
          end_at: ''
        };
      } catch (err) {
        ElMessage.error(err.msg || '预约失败');
      } finally {
        reserveLoading.value = false;
      }
    };
    const fetchOfflineComments = async () => {
      try {
        loadingOfflineComments.value = true;
        const res = await offlineCommentList({
          pageNum: offlineCommentPageNum.value,
          pageSize: offlineCommentPageSize.value,
          offline_id: id
        });
        offlineComments.value = res.data.items || [];
        offlineCommentTotal.value = res.data.total || 0;
        for (const item of offlineComments.value) {
          if (item.user_id && !userInfoMap.value[item.user_id]) {
            try {
              const userRes = await userOtherInfoService(item.user_id);
              userInfoMap.value[item.user_id] = userRes.data;
            } catch (e) {
              console.error('获取用户信息失败', e);
            }
          }
        }
      } catch (err) {
        ElMessage.error(err.msg || '门店评价加载失败');
      } finally {
        loadingOfflineComments.value = false;
      }
    };
    const handleOfflineCommentPageChange = newPage => {
      offlineCommentPageNum.value = newPage;
      fetchOfflineComments();
    };

    /**
     * 数据准备
     * */

    const fetchData = async () => {
      try {
        loading.value = true;
        let res;
        if (type === 'creation') {
          res = await creationInfo(id);
          detail.value = res.data;
          content.value = decodeHTML(res.data.content);
          const userRes = await userOtherInfoService(res.data.user_id);
          userInfo.value = userRes.data;
          const followRes = await userFollowInfoService(res.data.user_id);
          followState.value = followRes.data;
          await fetchLikeCollect();
        } else if (type === 'hairstyle') {
          res = await hairstyleInfo(id);
          detail.value = res.data;
          content.value = decodeHTML(res.data.content);
        } else if (type === 'offline') {
          res = await offlineInfo(id);
          detail.value = res.data;
          content.value = decodeHTML(res.data.offline_content);
        }
      } catch (error) {
        console.error('数据加载失败', error);
        ElMessage.error('数据加载失败');
      } finally {
        loading.value = false;
      }
    };
    const follow = async () => {
      if (followLoading.value) return;
      followLoading.value = true;
      try {
        let res;
        if (followState.value === 1) {
          res = await userFollowService(detail.value.user_id);
          if (res.code === 0) {
            ElMessage.success('关注成功');
            followState.value = 0;
          }
        } else {
          res = await userCancelFollowService(detail.value.user_id);
          if (res.code === 0) {
            ElMessage.success('取消关注成功');
            followState.value = 1;
          }
        }
      } catch (error) {
        ElMessage.error('操作失败');
      } finally {
        followLoading.value = false;
      }
    };
    const fetchComments = async () => {
      try {
        loadingComments.value = true;
        const res = await listComment({
          pageNum: commentPageNum.value,
          pageSize: commentPageSize.value,
          creation_id: Number(id)
        });
        comments.value = res.data.items;
        commentTotal.value = res.data.total;
      } catch (e) {
        ElMessage.error('评论加载失败');
      } finally {
        loadingComments.value = false;
      }
    };
    const submitComment = async () => {
      if (!commentInput.value.trim()) {
        return ElMessage.warning('评论内容不能为空');
      }
      try {
        await editorial({
          creation_id: Number(id),
          content: commentInput.value.trim()
        });
        commentInput.value = '';
        commentEditorRef.value?.editorRef?.clear();
        ElMessage.success('评论成功');
        fetchComments();
      } catch (e) {
        ElMessage.error('评论失败');
      }
    };
    const handleCommentPageChange = val => {
      commentPageNum.value = val;
      fetchComments();
    };
    const handleDelete = async cid => {
      try {
        await delComment(cid);
        ElMessage.success('已删除');
        fetchComments();
      } catch (e) {
        ElMessage.error('删除失败');
      }
    };
    const fechCurrentUserId = async () => {
      try {
        const res = await userInfoService();
        currentUserId.value = res.data.user_id;
      } catch (err) {
        console.error('获取当前用户失败');
      }
    };
    onMounted(() => {
      fetchData();
      fechCurrentUserId();
      if (type === 'creation') {
        fetchComments();
      }
      if (type === 'offline') {
        fetchOfflineComments();
        generateTimeOptions();
      }
    });
    const __returned__ = {
      route,
      router,
      detail,
      content,
      userInfo,
      followState,
      loading,
      followLoading,
      likeState,
      collectState,
      type,
      id,
      commentInput,
      comments,
      commentTotal,
      commentPageNum,
      commentPageSize,
      loadingComments,
      currentUserId,
      commentEditorRef,
      editDialogVisible,
      form,
      classOptions,
      formLoading,
      fetchClassOptions,
      openEditDialog,
      handleEditSubmit,
      handleCoverUpload,
      decodeHTML,
      fetchLikeCollect,
      toggleLike,
      toggleCollect,
      handleDeleteCreation,
      offlineComments,
      offlineCommentTotal,
      offlineCommentPageNum,
      offlineCommentPageSize,
      loadingOfflineComments,
      userInfoMap,
      reserveDialogVisible,
      memberOptions,
      reserveLoading,
      openReserveDialog,
      timeOptions,
      generateTimeOptions,
      submitReservation,
      fetchOfflineComments,
      handleOfflineCommentPageChange,
      fetchData,
      follow,
      fetchComments,
      submitComment,
      handleCommentPageChange,
      handleDelete,
      fechCurrentUserId,
      get useRoute() {
        return useRoute;
      },
      get useRouter() {
        return useRouter;
      },
      get creationInfo() {
        return creationInfo;
      },
      get likeInfo() {
        return likeInfo;
      },
      get likeCreation() {
        return likeCreation;
      },
      get collectInfo() {
        return collectInfo;
      },
      get collectCreation() {
        return collectCreation;
      },
      get listComment() {
        return listComment;
      },
      get editorial() {
        return editorial;
      },
      get delComment() {
        return delComment;
      },
      get delCreation() {
        return delCreation;
      },
      get updateCreation() {
        return updateCreation;
      },
      get creationClassList() {
        return creationClassList;
      },
      get hairstyleInfo() {
        return hairstyleInfo;
      },
      get offlineInfo() {
        return offlineInfo;
      },
      get memberList() {
        return memberList;
      },
      ref,
      onMounted,
      get userCancelFollowService() {
        return userCancelFollowService;
      },
      get userFollowInfoService() {
        return userFollowInfoService;
      },
      get userFollowService() {
        return userFollowService;
      },
      get userOtherInfoService() {
        return userOtherInfoService;
      },
      get userInfoService() {
        return userInfoService;
      },
      get offlineCommentList() {
        return offlineCommentList;
      },
      get addReservation() {
        return addReservation;
      },
      get ElMessage() {
        return ElMessage;
      },
      get ElMessageBox() {
        return ElMessageBox;
      },
      get Icon() {
        return Icon;
      },
      get enableCache() {
        return enableCache;
      },
      RichTextEditor,
      get ArrowLeft() {
        return ArrowLeft;
      },
      get Delete() {
        return Delete;
      },
      get Edit() {
        return Edit;
      },
      get uploadFile() {
        return uploadFile;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
