import { shallowRef, watch, onBeforeUnmount, nextTick } from 'vue';
import { Editor, Toolbar } from '@wangeditor/editor-for-vue';
import { uploadFile } from '@/api/fileUpload';
import { ElMessage } from 'element-plus';
export default {
  __name: 'RichTextEditor',
  props: {
    modelValue: String,
    visible: Boolean
  },
  emits: ['update:modelValue'],
  setup(__props, {
    expose: __expose,
    emit: __emit
  }) {
    __expose();
    const props = __props;
    const emit = __emit;
    const editorRef = shallowRef();
    const cleanEmptyParagraphs = html => {
      return html.replace(/<p>(\s|&nbsp;|　|<br\s*\/?>)*<\/p>/gi, '') // 清除各种空段落（空格、全角空格、&nbsp;、<br>）
      .trim();
    };
    const editorConfig = {
      placeholder: '请输入内容...',
      MENU_CONF: {
        uploadImage: {
