              insertFn(res.data, '', '');
              ElMessage.success('图片上传成功');
            } catch {
              ElMessage.error('图片上传失败');
            }
          }
        }
      }
    };
    const handleEditorChange = editor => {
      const rawHtml = editor.getHtml();
      const cleanedHtml = cleanEmptyParagraphs(rawHtml);
      emit('update:modelValue', cleanedHtml);
    };
    const handleEditorCreated = editor => {
      editorRef.value = editor;
      nextTick(() => {
        if (props.modelValue) {
          editor.setHtml(props.modelValue);
        }
      });
    };
    watch(() => props.visible, val => {
      if (!val && editorRef.value) {
        editorRef.value.destroy();
        editorRef.value = null;
      }
    });
    onBeforeUnmount(() => {
      if (editorRef.value) {
        editorRef.value.destroy();
        editorRef.value = null;
      }
    });
    const __returned__ = {
      props,
      emit,
      editorRef,
      cleanEmptyParagraphs,
      editorConfig,
      handleEditorChange,
      handleEditorCreated,
      shallowRef,
      watch,
      onBeforeUnmount,
      nextTick,
      get Editor() {
        return Editor;
      },
      get Toolbar() {
        return Toolbar;
      },
      get uploadFile() {
        return uploadFile;
      },
      get ElMessage() {
        return ElMessage;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
