import { useRoute } from 'vue-router';
import { creationInfo } from '@/api/creation';
import { hairstyleInfo } from '@/api/hairstyle';
import { offlineInfo } from '@/api/offline';
import { ref, onMounted } from 'vue';
import { userCancelFollowService, userFollowInfoService, userFollowService, userOtherInfoService } from "@/api/user";
import { ElMessage } from "element-plus";
export default {
  __name: 'contentPage',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const route = useRoute();
    const detail = ref({});
    const content = ref('');
    const userInfo = ref({});
    const followState = ref();
    const fetchData = async () => {
      const {
        type,
        id
      } = route.params;
      let res;
      let userRes;
      let followRes;
      if (type === 'creation') {
        res = await creationInfo(id);
        userRes = await userOtherInfoService(res.data.user_id);
        userInfo.value = userRes.data;
        followRes = await userFollowInfoService(res.data.user_id);
        followState.value = followRes.data;
      } else if (type === 'hairstyle') {
        res = await hairstyleInfo(id);
      } else if (type === 'offline') {
        res = await offlineInfo(id);
      }
      detail.value = res.data;
      content.value = res.data.content || res.data.offline_content;
    };
    const follow = async () => {
      try {
        let res;
        if (followState.value === 1) {
          res = await userFollowService(detail.value.user_id);
          if (res.code === 0) {
            ElMessage.success("关注成功！");
            followState.value = 0;
          }
        } else {
          res = await userCancelFollowService(detail.value.user_id);
          if (res.code === 0) {
            ElMessage.success("取消关注成功！");
            followState.value = 1;
          }
        }
      } catch (error) {
        console.error('关注或取消关注失败:', error);
        ElMessage.error('操作失败');
      }
    };
    onMounted(() => {
      fetchData();
    });
    const __returned__ = {
      route,
      detail,
      content,
      userInfo,
      followState,
      fetchData,
      follow,
      get useRoute() {
        return useRoute;
      },
      get creationInfo() {
        return creationInfo;
      },
      get hairstyleInfo() {
        return hairstyleInfo;
      },
      get offlineInfo() {
        return offlineInfo;
      },
      ref,
      onMounted,
      get userCancelFollowService() {
        return userCancelFollowService;
      },
      get userFollowInfoService() {
        return userFollowInfoService;
      },
      get userFollowService() {
        return userFollowService;
      },
      get userOtherInfoService() {
        return userOtherInfoService;
      },
      get ElMessage() {
        return ElMessage;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
